<?php if (!function_exists('automobile_mailchimp_list')) {    function automobile_mailchimp_list($apikey) {        global $automobile_var_options;        $MailChimp = new MailChimp($apikey);        $mailchimp_list = $MailChimp->call('lists/list');        return $mailchimp_list;    }}/** * @custom mail chimp form */if (!function_exists('automobile_custom_mailchimp')) {    function automobile_custom_mailchimp($under_construction = '0') {        global $automobile_var_options, $counter, $social_switch, $automobile_var_frame_static_text;        $automobile_var_enter_valid = isset($automobile_var_frame_static_text['automobile_var_enter_valid']) ? $automobile_var_frame_static_text['automobile_var_enter_valid'] : '';        $counter++;        ?>        <script>            function automobile_mailchimp_submit(theme_url, counter, admin_url) {                'use strict';                $ = jQuery;                $('#process_' + counter).html('<div id="process_newsletter_' + counter + '"><i class="icon-refresh icon-spin" style="float:right;margin:12px 0px 0px 0px;"></i></div>');                $.ajax({                    type: 'POST',                    url: admin_url,                    data: $('#mcform_' + counter).serialize() + '&action=automobile_mailchimp',                    success: function (response) {                        $('#mcform_' + counter).get(0).reset();                        $('#newsletter_mess_' + counter).fadeIn(600);                        $('#newsletter_mess_' + counter).html(response);                        //$('#btn_newsletter_'+counter).fadeIn(600);                        $('#process_' + counter).html('');                    }                });            }        </script>        <div id ="process_newsletter_<?php echo intval($counter); ?>">            <div id="process_<?php echo intval($counter); ?>" class="cs-show-msg"> </div>            <div id="newsletter_mess_<?php echo intval($counter); ?>" style="display:none" class="cs-error-msg"></div>             <div class="cs-form">                <form  action="javascript:automobile_mailchimp_submit('<?php echo get_template_directory_uri() ?>','<?php echo esc_js($counter); ?>','<?php echo admin_url('admin-ajax.php'); ?>')" id="mcform_<?php echo intval($counter); ?>" method="post">                    <div class="input-holder">						                        <input id="automobile_list_id<?php echo intval($counter); ?>" type="hidden" name="automobile_list_id" value="<?php                        if (isset($automobile_var_options['automobile_var_mailchimp_list'])) {                            echo esc_attr($automobile_var_options['automobile_var_mailchimp_list']);                        }                        ?>" />                        <input type="text" id="mc_email<?php echo intval($counter); ?>" class="txt-bar"  name="mc_email" placeholder=" <?php echo esc_html($automobile_var_enter_valid); ?>"   >                <!--                <input type="submit" class ="submit-btn cs-bgcolor vsmall" id="btn_newsletter_<?php // echo intval($counter);  ?>"   value="Submit">-->                        <label>                            <?php if ($under_construction == 0){                                ?>                                <i class="icon-send2"></i>                                <?php                            }?>                                                        <input class="cs-bgcolor" id="btn_newsletter_<?php echo intval($counter); ?>" type="submit" value="submit">                        </label>                    </div>                </form>            </div>            <?php if (isset($automobile_var_options['automobile_var_coming_social_switch']) == 'on' && $social_switch == 'on') {                ?>                <div class="cs-social-media">                    <?php                    if (function_exists('automobile_var_social_network')) {                        automobile_var_social_network('', 'yes');                    }                    ?>                </div>            <?php } ?>        </div>        <?php    }}